package main

import "fmt"

func diff(s, t byte) int {
	if s == t {
		return 0
	} else {
		return 1
	}
}
func min3(a, b, c int) int {
	if a <= b && a <= c {
		return a
	} else if b <= a && b <= c {
		return b
	} else {
		return c
	}
}
func isOneEditDistance1(s string, t string) bool {
	m := len(s)
	n := len(t)
	E := make([]int, (m+1)*(n+1))
	for i := 0; i < m+1; i++ {
		E[i] = i
	}
	for j := 0; j < n+1; j++ {
		E[j*(m+1)] = j
	}
	for j := 1; j < n+1; j++ {
		for i := 1; i < m+1; i++ {
			E[j*(m+1)+i] = min3(1+E[j*(m+1)+i-1], 1+E[(j-1)*(m+1)+i], diff(s[i-1], t[j-1])+E[(j-1)*(m+1)+i-1])
		}
	}
	return E[n*(m+1)+m] == 1
}

func abs(a int) int {
	if a < 0 {
		return -a
	}
	return a
}

func isOneEditDistance(s string, t string) bool {
	m := len(s)
	n := len(t)
	if abs(m-n) > 1 {
		return false
	}
	i := 0
	j := 0
	c := 0
	for i < m && j < n {
		//fmt.Printf("%v vs %v\n", i, j)
		if s[i] == t[j] {
			i++
			j++
		} else {
			c++
			if c > 1 {
				return false
			}
			if m > n {
				i++
			} else if m < n {
				j++
			} else {
				i++
				j++
			}
		}
	}
	if c == 0 {
		return abs(m-n) == 1
	}
	return true
}

func main() {
	fmt.Printf("%v\n", isOneEditDistance("a", ""))
	fmt.Printf("%v\n", isOneEditDistance("abc", "ac"))
	fmt.Printf("%v\n", isOneEditDistance("abc", "adc"))
	fmt.Printf("%v\n", isOneEditDistance("abc", "acd"))
	fmt.Printf("%v\n", isOneEditDistance("",
		""))
}
